import React from 'react'
import { useState} from 'react';
import { IconName } from "react-icons/md";

export default function Product(props) {
    const [color, setColor] = useState(props.color)

    let status = !props.status

    const statusChange = (status) => {
        props.power(props.index)
        setColor(colorSet(status))
        props.update(Math.random());
    }

    const colorSet = (status) => {
        if (status) {
            return 'green'
        }
        return 'red'
    } 

    return (
        <div>
            <button class="product" style={{color:color}} onClick={() => statusChange(status)}><img class="icon" src="my_icon.png" alt="my icon"/></button>
        </div>
    )
}
