import './App.css';
import Home from './components/Home';
import {BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import CreateRoom from './components/CreateRoom';
import Room from './components/Room';
import { useState } from 'react';
import { type } from '@testing-library/user-event/dist/type';

function App() {

  const [rooms, setRooms] = useState([])
  const [airCons, setAirCons] = useState([''])
  const [heaters, setHeaters] = useState([''])
  const [lights, setLights] = useState([''])
  const [stereos, setStereos] = useState([''])

  const createroom = (name,type,color) => {
    if (rooms.length != 0) {
      setRooms([...rooms,
        {
          name:name,
          type:type,
          color:color,
          items:0
        }
        ])
    }
    else {
      setRooms([
        {
          name:name,
          type:type,
          color:color,
          items:0
        }
        ])
    }
  }

  const updateItems = (name) => {
    for (let i = 0; i < rooms.length; i++) {
      if (rooms[i].name == name) {
        rooms[i].items += 1;
        break;
      }
    }
  }

  const addProducts = (name, product) => {
    if (product == 'ac') {
      airConPush(name)
    }
    else if (product == 'heater') {
      heaterPush(name)
    }
    else if (product == 'light') {
      lightPush(name)
    }
    else if (product == 'stereo') {
      stereoPush(name)
    }
    updateItems(name)
  }

  const airConPush = (name) => {
    setAirCons([...airCons,
    {name:"airCon-"+airCons.length+"-"+name,
    status:'red'}
    ])
    console.log(airCons)
  }

  const heaterPush = (name)=> {
      setHeaters([...heaters,
      {name:"heater-"+heaters.length+"-"+name,
      status:'red'}
      ])
  }

  const lightPush = (name)=> {
      setLights([...lights,
      {name:"light-"+lights.length+"-"+name,
      status:'red'}
      ])
  }

  const stereoPush = (name)=> {
      setStereos([...stereos,
      {name:"stereo-"+stereos.length+"-"+name,
      status:'red'}
      ])
  }

  return (
    <div className="App">
      <Router>
        {rooms.map((room) => {
          return <Link to={room.name}>{room.name}</Link>
        })}
        <Link to='/CreateRoom'>+</Link>
        <Routes>
          <Route exact path='/' element={<Home />} />
          <Route exact path='/CreateRoom' element={<CreateRoom createFunc={createroom}/>} />
          {rooms.map((room) => {
            return <Route exact path={'/'+room.name} element={
              <Room 
                name={room.name}
                type={room.type}
                color={room.color}
                items={room.items}
                airCons={airCons}
                heaters={heaters}
                lights={lights}
                stereos={stereos}
                addProductsFunc={addProducts}
              />
            } />
          })}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
