import React from 'react'
import { useState } from 'react'

export default function CreateProducts(props) {
    let product;

    const getProduct = (e) => {product = e.target.value}

    const clickHandle = () => {
        props.addDirect()
        props.addProductsFunc(props.name,product)
    }

    

    return (
        <div>
            <select name="product" id="product" onChange={getProduct}> 
                <option value="ac" id="ac">AC</option>
                <option value="light" id="light">Light</option>
                {props.heater}
                {props.stereo}
            </select>
            <br />
            <button onClick={clickHandle}>Add</button>
        </div>
    )
}
