import React from 'react'
import CreateProducts from './CreateProducts';
import { useState } from 'react';

export default function Room(props) {
    let flag = true;

    const [addProduct, setAddProduct] = useState()

    const addDirect = () => {
        let heater = false;
        let stereo = <option value="stereo" id="stereo">Stereo</option>;
        if (props.type == "bathroom") {
            heater=<option value="heater" id="heater">heater</option>;
        }
        if (props.items == 5) {
            window.alert("Reached the limit")
        }
        else if (flag == true) {
            setAddProduct(<CreateProducts name={props.name} addDirect={addDirect} addProductsFunc={props.addProductsFunc} items={props.items} heater={heater} stereo={stereo}/>)
            flag = false;
        }
        else {
            flag = true;
            setAddProduct();
        }
    }

    return (
        <div>
            <h3 style={{color:props.color}}>{props.name}</h3>
            <h4>{props.type}</h4>
            {props.airCons.map((airCon) => {
                return airCon
            })
            }
            {props.heaters.map((heater) => {
                return heater
            })
            }
            {props.lights.map((light) => {
                return light
            })
            }
            {props.stereos.map((stereo) => {
                return stereo
            })
            }
            <button onClick={addDirect}>+</button>
            {addProduct}
        </div>
    )
}
