import React from 'react'
import { useState,useEffect } from 'react';

export default function Product(props) {
    const [color, setColor] = useState(props.color)

    const statusChange = (status) => {
        props.power(props.name)
        // console.log(color)
        console.log("p "+props.status)
        setColor(colorSet(props.status))
        props.update();
    }

    const colorSet = (status) => {
        if (status) {
            return 'green'
        }
        return 'red'
    } 

    return (
        <div>
            <button style={{color:color}} onClick={() => statusChange(props.status)}>{props.displayName}</button>
        </div>
    )
}
