import React from 'react'
import CreateProducts from './CreateProducts';
import Product from './Product';
import { useState } from 'react';

export default function Room(props) {
    let flag = true;

    const [addProduct, setAddProduct] = useState()
    const [airConList, setAirConList] = useState([...props.airCons])
    const [heaterList, setHeaterList] = useState([...props.heaters])
    const [lightList, setLightList] = useState([...props.lights])
    const [stereoList, setStereoList] = useState([...props.stereos])

    const addDirect = () => {
        let heater = false;
        let stereo = <option value="stereo" id="stereo">Stereo</option>;
        if (props.type == "bathroom") {
            heater=<option value="heater" id="heater">heater</option>;
        }
        if (props.items == 5) {
            window.alert("Reached the limit")
        }
        else if (flag == true) {
            setAddProduct(<CreateProducts name={props.name} addDirect={addDirect} addProductsFunc={props.addProductsFunc} items={props.items} heater={heater} stereo={stereo}/>)
            flag = false;
        }
        else {
            flag = true;
            setAddProduct();
        }
    }

    const colorSet = (status) => {
        if (status) {
            return 'green'
        }
        else {
            return 'red'
        }
    }

    const updateLists = () => {
        setAirConList([...props.airCons])
        setHeaterList([...props.heaters])
        setLightList([...props.lights])
        setStereoList([...props.stereos])
    }

    return (
        <div>
            <h3 style={{color:props.color}}>{props.name}</h3>
            <h4>{props.type}</h4>
            {airConList.map((airCon) => {
                if (airCon == '') {
                    return
                }
                return <Product update={updateLists} status={airCon.status} color={colorSet(airCon.status)} name={airCon.name} power={props.airConPower}/>
            })
            }
            {heaterList.map((heater) => {
                if (heater == '') {
                    return
                }
                return <Product update={updateLists} status={heater.status} color={colorSet(heater.status)} name={heater.name} power={props.heaterPower}/>
            })
            }
            {lightList.map((light) => {
                if (light == '') {
                    return
                }
                return <Product update={updateLists} status={light.status} color={colorSet(light.status)} name={light.name} power={props.lightPower}/>
            })
            }
            {stereoList.map((stereo) => {
                if (stereo == '') {
                    return
                }
                return <Product update={updateLists} status={stereo.status} color={colorSet(stereo.status)} name={stereo.name} power={props.stereoPower}/>
            })
            }
            <button onClick={addDirect}>+</button>
            {addProduct}
        </div>
    )
}
