import React from 'react'
import CreateProducts from './CreateProducts';
import Product from './Product';
import { useState } from 'react';

export default function Room(props) {
    let flag = true;

    const [addProduct, setAddProduct] = useState()
    const [render, setRender] = useState()

    const addDirect = () => {
        let heater;
        let stereoOp = <option value="stereo" id="stereo">Stereo</option>;
        props.stereos.forEach(stereo => {
            if (stereo != '') {
                if (stereo.name.indexOf(props.roomName) != -1) {
                    stereoOp = '';
                }
            }
        });
        if (props.roomType == "bathroom") {
            heater=<option value="heater" id="heater">heater</option>;
        }
        if (props.items == 5) {
            window.alert("Reached the limit")
        }
        else if (flag == true) {
            setAddProduct(<CreateProducts name={props.roomName} addDirect={addDirect} addProductsFunc={props.addProductsFunc} items={props.items} heater={heater} stereo={stereoOp}/>)
            flag = false;
        }
        else {
            flag = true;
            setAddProduct();
        }
    }

    const colorSet = (status) => {
        if (status) {
            return 'green'
        }
        else {
            return 'red'
        }
    }

    const update = (i) => {
        setRender(i)
