        }
    }

    return (
        <div>
            <h3 style={{color:props.roomColor}}>{props.roomName.substring(0,props.roomName.indexOf('-'))}</h3>
            <h4>{props.roomType}</h4>
            <div id="product-grid">
            {props.airCons.map((airCon) => {
                let roomIndex = props.roomName.substring(props.roomName.indexOf('-'),props.roomName.indexOf('-')+2);
                let airConIndex = props.roomName.substring(props.roomName.indexOf('-'),props.roomName.indexOf('-')+2)
                if (airCon == '') {
                    return
                }
                if (airCon.name.substring(airCon.name,airCon.name.indexOf('-')) != props.roomName.substring(props.roomName.indexOf('-'),props.roomName.indexOf('-')+2)) {
                    return <Product update={update} status={airCon.status} color={colorSet(airCon.status)} name={airCon.name} displayName={airCon.name.substring(0,6)} power={props.airConPower}/>
                }
            })
            }
            {props.heaters.map((heater) => {
                if (heater == '') {
                    return
                }
                return <Product update={update} status={heater.status} color={colorSet(heater.status)} name={heater.name} displayName={heater.name.substring(0,6)} power={props.heaterPower}/>
            })
            }
            {props.lights.map((light) => {
                if (light == '') {
                    return
                }
                return <Product update={update} status={light.status} color={colorSet(light.status)} name={light.name} displayName={light.name.substring(0,5)} power={props.lightPower}/>
            })
            }
            {props.stereos.map((stereo) => {
                if (stereo == '') {
                    return
                }
                return <Product update={update} status={stereo.status} color={colorSet(stereo.status)} name={stereo.name} displayName={stereo.name.substring(0,6)} power={props.stereoPower}/>
            })
            }
            </div>
            <button onClick={addDirect}>+</button>
            {addProduct}
        </div>
    )
}
