import React from 'react'
import CreateProducts from './CreateProducts';
import { useState } from 'react';

export default function Room(props) {
    let airCons = [];
    let heaters = [];
    let lights = [];
    let stereos = [];

    let flag = true;

    const [addProduct, setAddProduct] = useState()

    const addDirect = () => {
        let heater = false;
        let stereo = <option value="stereo" id="stereo">Stereo</option>;
        if (props.type == "bathroom") {
            heater=<option value="heater" id="heater">heater</option>;
        }
        if (flag == true) {
            setAddProduct(<CreateProducts addProduct items={props.items} heater={heater} stereo={stereo}/>)
            flag = false;
        }
        else {
            setAddProduct();
        }
    }

    for (let i = 0; i < props.airCon; i++) {
        airCons.push(<button id={i} onClick={((e) => props.statusChange(e.target.id))}>{"airCon-"+i}</button>)
    }
    for (let i = 0; i < props.heater; i++) {
        heaters.push(<button id={i} onClick={((e) => props.statusChange(e.target.id))}></button>)
    }
    for (let i = 0; i < props.airCon; i++) {
        lights.push(<button id={i} onClick={((e) => props.statusChange(e.target.id))}></button>)
    }
    for (let i = 0; i < props.airCon; i++) {
        stereos.push(<button id={i} onClick={((e) => props.statusChange(e.target.id))}></button>)
    }

    return (
        <div>
            <h3 style={{color:props.color}}>{props.name}</h3>
            <h4>{props.type}</h4>
            {airCons.map((airCon) => {
                return airCon
            })
            }
            {heaters.map((heater) => {
                return heater
            })
            }
            {lights.map((light) => {
                return light
            })
            }
            {stereos.map((stereo) => {
                return stereo
            })
            }
            <button onClick={addDirect}>+</button>
            {addProduct}
        </div>
    )
}
