import './App.css';
import {BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import CreateRoom from './components/CreateRoom';
import Room from './components/Room';
import { useState } from 'react';
import { type } from '@testing-library/user-event/dist/type';

function App() {

  const [rooms, setRooms] = useState([])
  const [airCons, setAirCons] = useState([''])
  const [heaters, setHeaters] = useState([''])
  const [lights, setLights] = useState([''])
  const [stereos, setStereos] = useState([''])

  const createroom = (name,type,color) => {
    if (rooms.length != 0) {
      setRooms([...rooms,
        {
          name:name,
          index:rooms.length,
          type:type,
          color:color,
          items:0
        }
        ])
    }
    else {
      setRooms([
        {
          name:name,
          index:rooms.length,
          type:type,
          color:color,
          items:0
        }
        ])
    }
  }

  const updateItems = (name) => {
    for (let i = 0; i < rooms.length; i++) {
      if (rooms[i].name == name) {
        rooms[i].items += 1;
        break;
      }
    }
  }

  const addProducts = (name, product) => {
    if (product == 'ac') {
      airConPush(name)
    }
    else if (product == 'heater') {
      heaterPush(name)
    }
    else if (product == 'light') {
      lightPush(name)
    }
    else if (product == 'stereo') {
      stereoPush(name)
    }
    updateItems(name)
  }

  const airConPush = (name) => {
    airCons.push({name:"airCon-"+airCons.length+"-"+name,status:false})
    setAirCons([...airCons])
  }

  const heaterPush = (name)=> {
    heaters.push({name:"heater-"+heaters.length+"-"+name,
    status:false})
      setHeaters([...heaters])
  }

  const stereoPush = (name)=> {
    stereos.push({name:"stereo-"+stereos.length+"-"+name,
    status:false})
    setStereos([...stereos])
  }

  const lightPush = (name)=> {
    lights.push({name:"light-"+lights.length+"-"+name,
    status:false});
    setLights([...lights])
  }


  const airConPower = (acName) => {
    airCons.forEach(airCon => {
      if (airCon.name == acName) {
        airCon.status = !airCon.status;
        setAirCons(airCons)
      }
    });
  }

  const heaterPower = (heaterName) => {
    heaters.forEach(heater => {
      if (heater.name == heaterName) {
        heater.status = !heater.status;
        setHeaters(heaters)
      }
    });
  }

  const lightPower = (lightName) => {
    lights.forEach(light => {
      if (light.name == lightName) {
        light.status = !light.status;
        setLights(lights)
      }
    });
  }

  const stereoPower = (stereoName) => {
    stereos.forEach(stereo => {
      if (stereo.name == stereoName) {
        stereo.status = !stereo.status;
        setStereos(stereos)
      }
    });
  }

  return (
    <div className="App">
      <h1>Smart Home</h1>
      <Router>
        {rooms.map((room) => {
          return <button style={{backgroundColor: room.color}}><Link to={'/room/'+room.name.substring(0,room.name.indexOf('-'))}>{room.name.substring(0,room.name.indexOf('-'))}</Link></button>
        })}
        <button><Link to='/CreateRoom'>+</Link></button>
        <Routes>
          <Route exact path='/CreateRoom' element={<CreateRoom createFunc={createroom}/>} />
          {rooms.map((room) => {
            return <Route exact path={'/room/'+room.name.substring(0,room.name.indexOf('-'))} element={
              <Room 
                name={room.name}
                type={room.type}
                color={room.color}
                items={room.items}
                airCons={airCons}
                heaters={heaters}
                lights={lights}
                stereos={stereos}
                addProductsFunc={addProducts}
                stereoPower={stereoPower}
                lightPower={lightPower}
                heaterPower={heaterPower}
                airConPower={airConPower}
              />
            } />
          })}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
