import React from 'react'
import CreateProducts from './CreateProducts';
import Product from './Product';
import { useState } from 'react';

export default function Room(props) {
    let flag = true;

    const [addProduct, setAddProduct] = useState()
    const [airConList, setAirConList] = useState([])
    const [heaterList, setHeaterList] = useState([])
    const [lightList, setLightList] = useState([])
    const [stereoList, setStereoList] = useState([])

    // const productRender = (productList,productRenderList,power) => {
    //     let i = 0;
    //     productList.map = (product) => {
    //         productRenderList[i] = <Product status={product.status} name={Product.name} power={power}/>
    //         i++;
    //         return
    //     }
    // }
    const colorSet = (status) => {
        if (status) {
            return 'green'
        }
        else {
            return 'red'
        }
    }
    const airConRender = () => {
        let color;
        props.airCon.map = (airCon) => {
            color = colorSet(airCon.status)
            setAirConList(
                <Product color={color} name={airCon.name} power={props.airConPower}/>
            )
            return
        }
    }
    const heaterRender = () => {
        let color;
        props.heater.map = (heater) => {
            color = colorSet(heater.status)
            setHeaterList(
                <Product color={color} name={heater.name} power={props.heaterPower}/>
            )
            return
        }
    }
    const lightRender = () => {
        let color;
        props.light.map = (light) => {
            color = colorSet(light.status)
            setLightList(
                <Product color={color} name={light.name} power={props.lightPower}/>
            )
            return
        }
    }
    const stereoRender = () => {
        let color;
        props.stereo.map = (stereo) => {
            color = colorSet(stereo.status)
            setStereoList(
                <Product color={color} name={stereo.name} power={props.stereoPower}/>
            )
            return
        }
    }

    const addDirect = () => {
        let heater = false;
        let stereo = <option value="stereo" id="stereo">Stereo</option>;
        if (props.type == "bathroom") {
            heater=<option value="heater" id="heater">heater</option>;
        }
        if (props.items == 5) {
            window.alert("Reached the limit")
        }
        else if (flag == true) {
            setAddProduct(<CreateProducts name={props.name} addDirect={addDirect} addProductsFunc={props.addProductsFunc} items={props.items} heater={heater} stereo={stereo}/>)
            flag = false;
        }
        else {
            flag = true;
            setAddProduct();
        }
    }

    return (
        <div>
            <h3 style={{color:props.color}}>{props.name}</h3>
            <h4>{props.type}</h4>
            {airConList.map((airCon) => {
                if (airCon == '') {
                    return
                }
                return airCon
            })
            }
            {heaterList.map((heater) => {
                if (heater == '') {
                    return
                }
                return heater
            })
            }
            {lightList.map((light) => {
                if (light == '') {
                    return
                }
                return 
            })
            }
            {stereoList.map((stereo) => {
                if (stereo == '') {
                    return
                }
                return stereo
            })
            }
            <button onClick={addDirect}>+</button>
            {addProduct}
        </div>
    )
}
