import './App.css';
import Home from './components/Home';
import {BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import CreateRoom from './components/CreateRoom';
import Room from './components/Room';
import { useState } from 'react';
import { type } from '@testing-library/user-event/dist/type';

function App() {

  const [rooms, setRooms] = useState([])
  const [airCons, setAirCons] = useState([])
  const [heaters, setHeaters] = useState([])
  const [lights, setLights] = useState([])
  const [stereos, setStereos] = useState([])

  const createroom = (name,type,color) => {
    if (rooms.length != 0) {
      setRooms([...rooms,
        {
          name:name,
          type:type,
          color:color,
        }
        ])
    }
    else {
      setRooms([
        {
          name:name,
          type:type,
          color:color,
        }
        ])
    }
    console.log(rooms[rooms.length-1].name)
  }

  const addProducts = (name,items,airCon,heater,light,stereo) => {
    airConPush(airCon,name)
    heaterPush(heater,name)
    lightPush(light,name)
    stereoPush(stereo,name)
  }

  const airConPush = (airCon, name) => {
    for (let i = 0; i < airCon; i++) {
      setAirCons([...airCons,
      {name:"airCon-"+i+"-"+name},
      {status:false}
      ])
    }
  }

  const heaterPush = (heater, name)=> {
    for (let i = 0; i < heater; i++) {
      setHeaters([...heaters,
      {name:"heater-"+i+"-"+name,
      status:false}
      ])
    }
  }

  const lightPush = (light, name)=> {
    for (let i = 0; i < light; i++) {
      setLights([...lights,
      {name:"light-"+i+"-"+name,
      status:false}
      ])
    }
  }

  const stereoPush = (stereo, name)=> {
    for (let i = 0; i < stereo; i++) {
      setStereos([...stereos,
      {name:"stereo-"+i+"-"+name,
      status:false}
      ])
    }
  }

  return (
    <div className="App">
      <Router>
        {rooms.map((room) => {
          return <Link to={room.name}>aaa</Link>
        })}
        <Link to='/CreateRoom'>+</Link>
        <Routes>
          <Route exact path='/' element={<Home />} />
          <Route exact path='/CreateRoom' element={<CreateRoom createFunc={createroom}/>} />
          {rooms.map((room) => {
            return <Route exact path={'/'+room.name} component={() => {
              return <Room 
                name={room.name}
                type={room.type}
                airCon={room.airCon}
                heater={room.heater}
                light={room.light}
                stereo={room.stereo}
                addProductsFunc={addProducts}
              />
            }} />
          })}
        </Routes>
      </Router>
    </div>
  );
}

export default App;
