import React from 'react'
import CreateProducts from './CreateProducts';
import Product from './Product';
import { useState } from 'react';

export default function Room(props) {
    let flag = true;

    const [addProduct, setAddProduct] = useState()
    const [render, setRender] = useState()

    const addDirect = () => {
        let heaterOp;
        let stereoOp = <option value="stereo" id="stereo">Stereo</option>;
        props.stereos.forEach(stereo => {
            if (stereo != '') {
                console.log(stereo.name)
                if (stereo.name.indexOf(props.roomName) != -1) {
                    stereoOp = '';
                }
            }
        });
        if (props.roomType == "bathroom") {
            heaterOp=<option value="heater" id="heater">heater</option>;
        }
        if (props.roomItems == 5) {
            window.alert("Reached the limit")
        }
        else if (flag == true) {
            setAddProduct(<CreateProducts roomIndex={props.roomIndex} addDirect={addDirect} addProductsFunc={props.addProductsFunc} items={props.roomItems} heater={heaterOp} stereo={stereoOp}/>)
            flag = false;
        }
        else {
            flag = true;
            setAddProduct();
        }
    }

    const colorSet = (status) => {
        if (status) {
            return 'green'
        }
        else {
            return 'red'
        }
    }

    const update = (i) => {
        setRender(i)
    }

    const checkProductInRoom = (roomIndex,product,productPower,productName) => {
        if (product == '') {
            return
        }
        if (roomIndex == product.roomIndex) {
            return <Product update={update} status={product.status} color={colorSet(product.status)} index={product.index} displayName={productName} power={productPower}/>
        }
    }

    return (
        <div>
            <h3 style={{color:props.roomColor}}>{props.roomName.substring(0,props.roomName.indexOf('-'))}</h3>
            <h4>{props.roomType}</h4>
            <div id="product-grid">
            {props.airCons.map((airCon) => checkProductInRoom(props.roomIndex,airCon,props.airConPower,"AC"))
            }
            {props.heaters.map((heater) => checkProductInRoom(props.roomIndex,heater,props.heaterPower,"Heater"))
            }
            {props.lights.map((light) => checkProductInRoom(props.roomIndex,light,props.lightPower,"Light"))
            }
            {props.stereos.map((stereo) => checkProductInRoom(props.roomIndex,stereo,props.stereoPower,"Stereo"))
            }
            </div>
            <button onClick={addDirect}>+</button>
            {addProduct}
        </div>
    )
}
