import React from 'react'
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

export default function CreateProducts(props) {
    const [heater, setHeater] = useState();
    const [stereo, setStereo] = useState();

    const stereoOption = () => {
        let stereo = <option value="stereo" id="stereo">Stereo</option>
        if (props.stereo) {
            setStereo(stereo)
        }
    }
        
    const heaterOption = () => {
        let heater = <option value="heater" id="heater">heater</option>
        if (props.Heater) {
            setHeater(heater)
        }
    }

    return (
        <div>
            {() => heaterOption}
            {stereoOption}
            <select name="type" id="type"> 
                <option value="ac" id="ac">AC</option>
                <option value="light" id="light">Light</option>
                {heater}
                {stereo}
            </select>
        </div>
    )
}
