import React from 'react'
import { useState } from 'react'

export default function CreateProducts(props) {
    const [product, setProduct] = useState('ac')

    const getProduct = (e) => setProduct(e.target.value)

    return (
        <div>
            <select name="product" id="product" onChange={getProduct}> 
                <option value="ac" id="ac">AC</option>
                <option value="light" id="light">Light</option>
                {props.heater}
                {props.stereo}
            </select>
            <br />
            <button onClick={() => {
                props.addDirect()
                props.addProductFunc(props.name,product)
                }}>Add</button>
        </div>
    )
}
