import React from 'react'
import CreateProducts from './CreateProducts';
import Product from './Product';
import { useState } from 'react';

export default function Room(props) {
    let flag = true;

    const [addProduct, setAddProduct] = useState()
    const [render, setRender] = useState()

    const addDirect = () => {
        let heater;
        let stereo = <option value="stereo" id="stereo">Stereo</option>;
        props.stereos.forEach(stereo => {
            if (stereo != '') {
                if (stereo.name.indexOf(props.name) != -1) {
                    stereo = '';
                }
            }
        });
        if (props.type == "bathroom") {
            heater=<option value="heater" id="heater">heater</option>;
        }
        if (props.items == 5) {
            window.alert("Reached the limit")
        }
        else if (flag == true) {
            setAddProduct(<CreateProducts name={props.name} addDirect={addDirect} addProductsFunc={props.addProductsFunc} items={props.items} heater={heater} stereo={stereo}/>)
            flag = false;
        }
        else {
            flag = true;
            setAddProduct();
        }
    }

    const colorSet = (status) => {
        if (status) {
            return 'green'
        }
        else {
            return 'red'
        }
    }

    const update = (i) => {
        setRender(i)
    }

    return (
        <div>
            <h3 style={{color:props.color}}>{props.name}</h3>
            <h4>{props.type}</h4>
            {props.airCons.map((airCon) => {
                if (airCon == '') {
                    return
                }
                if (airCon.name.indexOf(props.name) != -1) {
                    return <Product update={update} status={airCon.status} color={colorSet(airCon.status)} name={airCon.name} displayName={airCon.name.substring(0,6)} power={props.airConPower}/>
                }
            })
            }
            {props.heaters.map((heater) => {
                if (heater == '') {
                    return
                }
                return <Product update={update} status={heater.status} color={colorSet(heater.status)} name={heater.name} displayName={heater.name.substring(0,6)} power={props.heaterPower}/>
            })
            }
            {props.lights.map((light) => {
                if (light == '') {
                    return
                }
                return <Product update={update} status={light.status} color={colorSet(light.status)} name={light.name} displayName={light.name.substring(0,5)} power={props.lightPower}/>
            })
            }
            {props.stereos.map((stereo) => {
                if (stereo == '') {
                    return
                }
                return <Product update={update} status={stereo.status} color={colorSet(stereo.status)} name={stereo.name} displayName={stereo.name.substring(0,6)} power={props.stereoPower}/>
            })
            }
            <button onClick={addDirect}>+</button>
            {addProduct}
        </div>
    )
}
